mapper.select:
select size ,min(salesPrice) as saleBid from sales where modelNum = #{modelNum} and salesCode not in (select d.salesCode from deal d)
and size = #{size}
group by size

select size ,max(buyPrice) as buyBid from buy where modelNum = #{modelNum} and buyCode not in (select d.buyCode from deal d)
and size = #{size}
group by size
